import React, { useState } from "react";

function CacheControl() {
  const [lastFetch, setLastFetch] = useState(null);
  const [data, setData] = useState([]);

  const fetchData = async (cacheControl = "no-cache") => {
    const response = await fetch("https://jsonplaceholder.typicode.com/posts", {
      headers: {
        "Cache-Control": cacheControl,
      },
    });
    const posts = await response.json();
    setData(posts.slice(0, 5)); // Display only the first 5 posts for simplicity
    setLastFetch(new Date().toISOString());
    alert(`Data fetched with Cache-Control: ${cacheControl}`);
  };

  return (
    <div>
      <button onClick={() => fetchData()}>Fetch Data with No Cache</button>
      <button onClick={() => fetchData("public, max-age=604800")}>
        Fetch Data with Cache
      </button>
      <div>
        {data.map((post) => (
          <p key={post.id}>{post.title}</p>
        ))}
      </div>
      <p>Last fetched at: {lastFetch || "Never"}</p>
    </div>
  );
}

export default CacheControl;
