import React, { useState } from "react";

function ImageCarousel() {
  const images = [
    { url: "/ronaldo.jpeg", alt: "Ronaldo" },
    { url: "/uefa.jpeg", alt: "UEFA" },
    { url: "/ozil.jpeg", alt: "Ozil" },
    { url: "/neymar.jpeg", alt: "NEymar" },
    { url: "/all.jpeg", alt: "All" },
    { url: "/messi.jpeg", alt: "Messi" },
  ];

  const [selectedImageIndex, setSelectedImageIndex] = useState(0);
  const [imageWidth, setImageWidth] = useState(500);
  const [imageHeight, setImageHeight] = useState(300);
  const [fit, setFit] = useState("contain");
  const [format, setFormat] = useState("webp");
  const [description, setDescription] = useState(
    "Contains the image within the frame, maintaining its aspect ratio. May leave empty space on two sides."
  );

  const baseUrl = "https://mayank-cdn-test.netlify.app/.netlify/images";

  const buildImageUrl = () => {
    const { url } = images[selectedImageIndex];
    return `${baseUrl}?url=${url}&w=${imageWidth}&h=${imageHeight}&fit=${fit}&fm=${format}`;
  };

  const handleFitChange = (event) => {
    setFit(event.target.value);
    switch (event.target.value) {
      case "contain":
        setDescription(
          "Contains the image within the frame, maintaining its aspect ratio. May leave empty space on two sides."
        );
        break;
      case "cover":
        setDescription(
          "Covers the entire frame with the image, cropping it if necessary to maintain its aspect ratio."
        );
        break;
      case "fill":
        setDescription(
          "Stretches or squishes the image to fill the frame, ignoring its aspect ratio."
        );
        break;
      default:
        setDescription("");
    }
  };

  return (
    <div>
      <div style={{ marginTop: "50px", border: "1px solid black" }}>
        <img
          src={buildImageUrl()}
          alt={images[selectedImageIndex].alt}
          style={{
            margin: "auto",
            maxWidth: "100%",
            height: "auto",
          }}
        />
      </div>
      <div
        style={{
          position: "absolute",
          bottom: 10,
        }}
      >
        {images.map((img, index) => (
          <button key={index} onClick={() => setSelectedImageIndex(index)}>
            <img
              src={img.url}
              alt={img.alt}
              width={100}
              height={100}
              style={{ display: "block", margin: "auto" }}
            />
          </button>
        ))}
      </div>
      <div
        style={{ display: "flex", justifyContent: "right", marginTop: "20px" }}
      >
        <input
          type="number"
          value={imageWidth}
          onChange={(e) => setImageWidth(e.target.value)}
        />
        <input
          type="number"
          value={imageHeight}
          onChange={(e) => setImageHeight(e.target.value)}
        />
        <select value={format} onChange={(e) => setFormat(e.target.value)}>
          <option value="contain">Contain</option>
          <option value="cover">Cover</option>
          <option value="fill">Fill</option>
        </select>
        <select value={format} onChange={(e) => setFormat(e.target.value)}>
          <option value="webp">WebP</option>
          <option value="jpg">JPG</option>
          <option value="png">PNG</option>
          <option value="avif">AVIF</option>
        </select>
      </div>
      <div style={{ marginTop: "20px" }}>
        <p>{description}</p>
      </div>
    </div>
  );
}

export default ImageCarousel;
