import React, { useState } from "react";
import {
  Box,
  Typography,
  Button,
  TextField,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Paper,
} from "@mui/material";
import { styled } from "@mui/material/styles";

// Neumorphic styled Paper component
const NeumorphicPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  borderRadius: theme.spacing(2),
  boxShadow: "20px 20px 60px #bebebe, -20px -20px 60px #ffffff",
}));
function ImageCarousel() {
  const images = [
    { url: "/modric.jpeg", alt: "Modric" },
    { url: "/madrid.jpeg", alt: "Madrid" },
    { url: "/ozil.jpeg", alt: "Ozil" },
    { url: "/ronaldo-1.jpeg", alt: "Ronaldo" },
    { url: "/benzema.jpeg", alt: "Benzema" },
    { url: "/messi.jpeg", alt: "Messi" },
  ];

  const [selectedImageIndex, setSelectedImageIndex] = useState(0);
  const [imageWidth, setImageWidth] = useState(500);
  const [imageHeight, setImageHeight] = useState(300);
  const [fit, setFit] = useState("contain");
  const [format, setFormat] = useState("webp");

  const baseUrl = "https://mayank-cdn-test.netlify.app/.netlify/images";

  const buildImageUrl = () => {
    const { url } = images[selectedImageIndex];
    return `${baseUrl}?url=${encodeURIComponent(
      url
    )}&w=${imageWidth}&h=${imageHeight}&fit=${fit}&fm=${format}`;
  };

  const handleFitChange = (event) => {
    setFit(event.target.value);
  };

  return (
    <Box sx={{ p: 4, border: "1px solid grey", borderRadius: 2 }}>
      <Box
        sx={{
          width: 500,
          height: 300,
          overflow: "hidden",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          borderRadius: 1,
          mb: 2,
          borderColor: "primary.main",
        }}
      >
        <img
          src={buildImageUrl()}
          alt={images[selectedImageIndex].alt}
          style={{
            width: fit === "fill" ? "100%" : "auto",
            height: fit === "fill" ? "100%" : "auto",
            maxWidth: fit === "contain" || fit === "cover" ? "100%" : "none",
            maxHeight: fit === "contain" || fit === "cover" ? "100%" : "none",
            objectFit: fit,
          }}
        />
      </Box>
      <Box
        sx={{
          display: "flex",
          gap: 1,
          flexWrap: "wrap",
          justifyContent: "center",
          mb: 2,
        }}
      >
        {images.map((img, index) => (
          <Button
            key={index}
            onClick={() => setSelectedImageIndex(index)}
            variant="outlined"
            sx={{
              p: 0,
              border:
                selectedImageIndex === index
                  ? "2px solid blue"
                  : "1px solid grey",
              transform: selectedImageIndex === index ? "scale(1.1)" : "none",
              transition: "transform 0.3s, border 0.3s", // Smooth transition for scale and border color change
            }}
          >
            <img
              src={img.url}
              alt={img.alt}
              width={100}
              height={100}
              style={{ display: "block" }}
            />
          </Button>
        ))}
      </Box>
      <Box sx={{ display: "flex", gap: 2, justifyContent: "right", mb: 2 }}>
        <TextField
          label="Width"
          type="number"
          value={imageWidth}
          onChange={(e) => setImageWidth(e.target.value)}
          variant="outlined"
          size="small"
        />
        <TextField
          label="Height"
          type="number"
          value={imageHeight}
          onChange={(e) => setImageHeight(e.target.value)}
          variant="outlined"
          size="small"
        />
        <FormControl size="small">
          <InputLabel>Fit</InputLabel>
          <Select value={fit} label="Fit" onChange={handleFitChange}>
            <MenuItem value="contain">Contain</MenuItem>
            <MenuItem value="cover">Cover</MenuItem>
            <MenuItem value="fill">Fill</MenuItem>
          </Select>
        </FormControl>
        <FormControl size="small">
          <InputLabel>Format</InputLabel>
          <Select
            value={format}
            label="Format"
            onChange={(e) => setFormat(e.target.value)}
          >
            <MenuItem value="webp">WebP</MenuItem>
            <MenuItem value="jpg">JPG</MenuItem>
            <MenuItem value="png">PNG</MenuItem>
            <MenuItem value="avif">AVIF</MenuItem>
          </Select>
        </FormControl>
      </Box>
      <Typography variant="body2" color="text.secondary">
        Description based on selected fit:{" "}
        {fit === "contain"
          ? "Resizes the image to fit within the given dimension while preserving its aspect ratio."
          : fit === "cover"
          ? "Resizes the image to fill the given dimension while preserving its aspect ratio, but may crop the image."
          : "Stretches the image to fit the content box, regardless of its aspect ratio."}
      </Typography>
    </Box>
  );
}

export default ImageCarousel;
