import React, { useState } from "react";
import "./App.css";

function Blobs() {
  const [inputKey, setInputKey] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [message, setMessage] = useState("");
  const [fetchedData, setFetchedData] = useState("");

  const handleInputChange = (e, setter) => setter(e.target.value);

  const handleSetData = async () => {
    const response = await fetch("/.netlify/functions/setBlob", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ key: inputKey, value: inputValue }),
    });
    const data = await response.json();
    setMessage(data.message);
  };

  const handleGetData = async () => {
    const response = await fetch(`/.netlify/functions/getBlob?key=${inputKey}`);
    const data = await response.json();
    setFetchedData(JSON.stringify(data));
  };

  const handleDeleteData = async () => {
    const response = await fetch("/.netlify/functions/deleteBlob", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ key: inputKey }),
    });
    const data = await response.json();
    setMessage(data.message);
    setFetchedData("");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Netlify Blobs Demo</h1>
        <div>
          <input
            value={inputKey}
            onChange={(e) => handleInputChange(e, setInputKey)}
            placeholder="Enter key"
          />
          <input
            value={inputValue}
            onChange={(e) => handleInputChange(e, setInputValue)}
            placeholder="Enter value"
          />
          <button onClick={handleSetData}>Set Data</button>
          <button onClick={handleGetData}>Get Data</button>
          <button onClick={handleDeleteData}>Delete Data</button>
        </div>
        {message && <p>{message}</p>}
        {fetchedData && (
          <div>
            <h3>Fetched Data:</h3>
            <p>{fetchedData}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default Blobs;
