// src/components/ImageManager.js
import React, { useState, useEffect } from "react";

function ImageManager() {
  const [file, setFile] = useState(null);
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetchImages();
  }, []);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file first!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("/.netlify/functions/uploadImage", {
        method: "POST",
        body: formData,
      });
      console.log("response", response);
      if (response.ok) {
        alert("Upload Successful!");
        fetchImages(); // Fetch the latest images list including the new upload
      } else {
        const error = await response.json();
        alert(`Upload failed: ${error.message}`);
      }
    } catch (error) {
      alert("Upload failed:", error.message);
    }
  };

  const fetchImages = async () => {
    const response = await fetch("/.netlify/functions/getImage");
    if (response.ok) {
      const data = await response.json();
      setImages(data.images || []);
    }
  };

  return (
    <div>
      <h2>Upload and Manage Images</h2>
      <input type="file" onChange={handleFileChange} accept="image/*" />
      <button onClick={handleUpload}>Upload Image</button>
      <div>
        <h3>Uploaded Images</h3>
        {images.length > 0 ? (
          images.map((image, index) => (
            <div key={index}>
              <img
                src={`data:image/jpeg;base64,${image.data}`}
                alt={image.metadata.filename}
                style={{ width: "100px", height: "auto" }}
              />
              <p>Filename: {image.metadata.filename}</p>
              <p>MIME type: {image.metadata.mimetype}</p>
            </div>
          ))
        ) : (
          <p>No images to display.</p>
        )}
      </div>
    </div>
  );
}

export default ImageManager;
